import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const AttachmentsModal = () => {
  const { contratId } = useParams();
  const [attachments, setAttachments] = useState([]);
  const [newAttachments, setNewAttachments] = useState([]);

  // Function to add new attachments to the list
  const handleAddAttachments = (files) => {
    setNewAttachments(files);
  };

  // Function to delete an attachment from the list
  const handleDeleteAttachment = (index) => {
    const updatedAttachments = [...attachments];
    updatedAttachments.splice(index, 1);
    setAttachments(updatedAttachments);
  };

  // Function to update attachments
  const handleUpdateAttachments = async () => {
    // Assuming you have an API endpoint to update attachments on the server
    const apiUrl = `/api/contracts/${contratId}/attachments`;

    try {
      const response = await fetch(apiUrl, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ attachments: [...attachments, ...newAttachments] }),
      });

      if (response.ok) {
        // Update the state with the new list of attachments
        setAttachments([...attachments, ...newAttachments]);
        setNewAttachments([]); // Clear the newAttachments after updating
        console.log('Attachments updated successfully');
      } else {
        console.error('Failed to update attachments');
      }
    } catch (error) {
      console.error('Error updating attachments:', error);
    }
  };

  return (
    <div>
      <h2>Attachments</h2>

      {/* Display existing attachments */}
      <div>
        {attachments.map((attachment, index) => (
          <div key={index}>
            <img
              src={attachment.url}
              style={{ width: '200px', height: '200px' }}
              alt={`Attachment ${index + 1}`}
              className='rounded'
            />
            <button onClick={() => handleDeleteAttachment(index)}>
              Delete
            </button>
          </div>
        ))}
      </div>

      {/* File input for adding new attachments */}
      <input
        type="file"
        onChange={(e) => handleAddAttachments(Array.from(e.target.files))}
        multiple
      />

      {/* Button to update attachments */}
      <button onClick={handleUpdateAttachments}>
        Update Attachments
      </button>
    </div>
  );
};

export default AttachmentsModal;
