import React, { useState } from 'react';

const AttachmentsModal = ({ isOpen, onClose, existingAttachments, onDeleteAttachment, onAddAttachments }) => {
    const [newAttachments, setNewAttachments] = useState([]);

    const handleDelete = (attachmentId) => {
        onDeleteAttachment(attachmentId);
    };

    const handleAdd = () => {
        // Implement logic to add newAttachments
        onAddAttachments(newAttachments);
        setNewAttachments([]); // Clear the newAttachments after adding
    };

    return (
        <div className="fixed top-0 left-0 z-10 flex h-full w-full items-center justify-center">
            <div className="relative transform overflow-hidden rounded-lg bg-white text-left shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg rounded-md bg-white p-8 border-2 shadow-lg border-tunisys-100 dark:bg-gray-900">
                <div className={`modal ${isOpen ? 'open' : 'closed'}`}>
                    <div className="modal-content">
                        <h2>Attachments</h2>

                        {/* Display existing attachments */}
                        <div className="grid grid-cols-2 gap-4">
                            {existingAttachments.map((attachment) => (
                                <div key={attachment._id} className="relative">
                                    <button
                                        className="absolute top-2 right-2 text-red-600"
                                        onClick={() => handleDelete(attachment._id)}
                                    >
                                        X
                                    </button>
                                    <img
                                        src={attachment.url}
                                        style={{ width: '200px', height: '200px' }}
                                        alt={`Attachment ${attachment._id}`}
                                        className='rounded'
                                    />
                                </div>
                            ))}
                        </div>

                        {/* Display new attachments */}
                        <div className="grid grid-cols-2 gap-4 mt-4">
                            {newAttachments.map((newAttachment, index) => (
                                <div key={index} className="relative">
                                    <img
                                        src={URL.createObjectURL(newAttachment)}
                                        style={{ width: '200px', height: '200px' }}
                                        alt={`New Attachment ${index + 1}`}
                                        className='rounded'
                                    />
                                </div>
                            ))}
                        </div>

                        {/* File input for adding new attachments */}
                        <input
                            type="file"
                            onChange={(e) => setNewAttachments([...newAttachments, ...e.target.files])}
                            multiple
                        />


                        {/* Button to add new attachments */}
                        <button onClick={handleAdd}>
                            Add Attachments
                        </button>

                        {/* Close button */}
                        <button onClick={onClose}>
                            Close
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AttachmentsModal;
