import React, { useState, useEffect } from "react";
import EquipementList from "views/Modals/EquipementList";
import TechnicienList from "views/Modals/ListeTechnicien";
import axios from "axios"; // Correction : axios doit être importé depuis 'axios', pas 'react'
import { toast, ToastContainer } from "react-toastify";
const AddFieldTicket = () => {
  const type = "FIELD";
  const status = "ASSIGNED"
  const [users, setUsers] = useState([]);
  const [emptyFields, setEmptyFields] = useState([]);
  const [note, setNote] = useState("");
  const [call_time, setCallTime] = useState("");
  const [service_station, setservice_station] = useState("");
  const [service_type, setServiceType] = useState("");
  const [reference, setReferenceNumber] = useState("");
  const [isTechnicienModalOpen, setTechnicienModalOpen] = useState(false);
  const [isEquipementModalOpen, setEquipementModalOpen] = useState(false);
  //// Helpdesk ////
  const [helpdesk, setHelpdesk] = useState("");
  const [nameHelpdesk, setNameHelpdesk] = useState("");
  const [lastname, setLastName] = useState("");
  /// Equipement Data //////
  const [equipement, setequipement] = useState('')
  const [selectedEquipementSn, setselectedEquipementSn] = useState([])
  const [equipement_type, setEquipementType] = useState("");
  const [client, setClient] = useState('');
  const [clientId, setClientId] = useState('');
  const [StartGarantie_Date, setStartGarantieDate] = useState("");
  const [EndGarantie_Date, setEndGarantieDate] = useState("");
  const [service_status, setServiceStatus] = useState("");
  ////////////////////////////////////////////////////////
  const [successMessage, setSuccessMessage] = useState("");
  const [error, setError] = useState(null);
  const handleHelpDeskSelection = (helpdesk) => {
    setHelpdesk(helpdesk._id)
    setNameHelpdesk(helpdesk.firstname)
    setLastName(helpdesk.lastname)
  }
  const handleEquipementSelection = (equipement) => {
    setequipement(equipement._id)
    setservice_station(equipement.adresse)
    setServiceStatus(equipement.status)

    if (equipement.client && equipement.client.length > 0) {
      equipement.client.forEach((item, index) => {
        console.log(`Element ${index}:`, item);
      });
      const firstClient = equipement.client[0];
      setClient(firstClient.client || '');
      setClientId(firstClient._id || '');
    } else {
      setClient('');
      setClientId('');
    }
    if (equipement.service && equipement.service.length > 0) {
      const formattedEffectiveDate = new Date(equipement.service[0].effective_date || '').toISOString().split('T')[0];
      setStartGarantieDate(formattedEffectiveDate);
      const formattedTerminationDate = new Date(equipement.service[0].termination_date || '').toISOString().split('T')[0];
      setEndGarantieDate(formattedTerminationDate)
    }
    setEquipementType(equipement.equipement_type)

    setselectedEquipementSn(equipement.equipement_sn);
    console.log("Selected equipement ID:", equipement._id);
    closeEquipementModal();
  };

  const [contacts, setContact] = useState("")
  const [selectedContact, setSelectedContact] = useState("");


  useEffect(() => {
    if (clientId) {
      fetchContacts();
    }
  }, [clientId]);

  // Define fetchContacts function
  const fetchContacts = async () => {
    try {
      const response = await fetch(`/api/client/contact/${clientId}`);
      const contactData = await response.json();

      if (response.ok) {
        setContact(contactData);
      } else {
        console.error("Erreur lors de la récupération des utilisateurs");
      }
    } catch (error) {
      console.error("Erreur lors de la récupération des utilisateurs", error);
    }
  };

  const openTechnicienModal = () => {
    setTechnicienModalOpen(true);
  };

  const closeTechnicienModal = () => {
    setTechnicienModalOpen(false);
  };

  const openEquipementModal = () => {
    setEquipementModalOpen(true);
  };

  const closeEquipementModal = () => {
    setEquipementModalOpen(false);
  };

  const [selectedContactId, setSelectedContactId] = useState('');

  const handleContactChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedContact(selectedValue);
    setSelectedContactId(selectedValue);
    console.log(selectedContactId)
  };;
  const handleSubmit = async (e) => {
    e.preventDefault();
    const ticketData = {
      equipement,
      technicien: helpdesk,
      service_type,
      equipement_type,
      garantie_end_date: EndGarantie_Date,
      garantie_start_date: StartGarantie_Date,
      service_station,
      contact: selectedContactId,
      service_status,
      client: clientId,
      note,
      call_time,
      type,
      status,
      reference
    };
    const response = await fetch('/api/ticket/phone', {
      method: 'POST',
      body: JSON.stringify(ticketData),
      headers: {
        'Content-type': 'application/json'
      }
    });
    const json = await response.json();
    if (!response.ok) {
      console.log(json);
      setError(json.error);
      setEmptyFields(json.emptyFields);
    } else {
      setEmptyFields([]);
      setClient('');
      setselectedEquipementSn('')
      setHelpdesk('')
      setServiceStatus('')
      setContact('')
      setEquipementType('')
      setStartGarantieDate('')
      setEndGarantieDate('')
      setHelpdesk('') 
      setReferenceNumber('')  
      setServiceType('')
      setservice_station('')
      setCallTime('')
      setNote('')
      setError(null);
      setSuccessMessage("Ticket ajouté avec succès");
      console.log('Request sent successfully');
      setEmptyFields([]);
      toast.success('Ticket ajouté avec succès');
      setError(null);
    }
  }
  return (
    <>
      <ToastContainer />
      <h1 className="mx-0  mb-2.5 text-center text-[1.7em] font-semibold text-navy-700 dark:text-gray-500">
        Ajouter Field Ticket :
      </h1>
      <div class="space-y-12">
        <div class="border-b border-gray-900/10 pb-12">
          <div class="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
            <div class="sm:col-span-3">
              <label
                for="first-name"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Equipement S/N
              </label>
              <div class="mt-2">
                <input
                  value={equipement}
                  onChange={(e) => setEquipement(e.target.value)}
                  type="text"
                   onClick={openEquipementModal}
                  name="first-name"
                  id="first-name"
                  autocomplete="given-name"
                  class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
                />
              </div>
            </div>
            <div class="sm:col-span-3">
              <label
                for="last-name"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Client
              </label>
              <div class="mt-2">
                <input
                  //  value={client}
                  //  onChange={(e) => setClient(e.target.value)}
                  type="text"
                  name="last-name"
                  id="last-name"
                  autocomplete="family-name"
                  class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
                />
              </div>
            </div>
            <div className="sm:col-span-3">
              <label
                htmlFor="user-list"
                className="block font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Technicien
              </label>
              <div className="mt-2">
              <input
                 onClick={openTechnicienModal}
                  value={technicien}
                  type="text" // Change the type to datetime-local

                  class="w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:block sm:leading-6"
                />

              </div>
            </div>
            <div class="sm:col-span-3">
              <label
                for="call-in-time"
                class="block font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Call in time
              </label>
              <div class="mt-2">
                <input
                  value={calltime}
                  onChange={(e) => setCallTime(e.target.value)}
                  type="datetime-local" // Change the type to datetime-local
                  name="call-in-time" // Provide a unique name for the input
                  id="call-in-time"
                  class="w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:block sm:leading-6"
                />
              </div>
            </div>
            <div class="sm:col-span-3">
              <label
                for="service-type"
                class="block font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Service Type
              </label>
              <div class="mt-2">
                <select
                  value={service_type}
                  onChange={(e) => setServiceType(e.target.value)}
                  name="service-type"
                  id="service-type"
                  class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
                >
                  <option value="option1">Corrective Maintenance</option>
                  <option value="option2">Preventive Maintenance</option>
                  <option value="option3">Staging Service</option>
                  <option value="option3">Installation-Onsite inpection</option>
                  <option value="option3">
                    Installation-Physical Installation
                  </option>
                  <option value="option3">
                    Installation-Online Activation
                  </option>
                  <option value="option3">Hardware-Online update</option>
                  <option value="option3">Software-upgrade update</option>
                  <option value="option3">Removal</option>
                  <option value="option3">Disable</option>
                  <option value="option3">Other</option>
                </select>
              </div>
            </div>
            <div class="sm:col-span-3">
              <label
                for="first-name"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Service Status
              </label>
              <div class="mt-2">
                <input
                  value={service_status}
                  onChange={(e) => setServiceStatus(e.target.value)}
                  type="text"
                  name="first-name"
                  id="first-name"
                  autocomplete="given-name"
                  class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
                />
              </div>
            </div>
            <div class="sm:col-span-3">
              <label
                for="first-name"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Reference number
              </label>
              <div class="mt-2">
                <input
                  value={reference_number}
                  onChange={(e) => setReferenceNumber(e.target.value)}
                  type="text"
                  name="first-name"
                  id="first-name"
                  autocomplete="given-name"
                  class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
                />
              </div>
            </div>
            <div class="sm:col-span-3">
              <label
                for="last-name"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Contact Person
              </label>
              <div class="mt-2">
                <input
                  value={contact}
                  onChange={(e) => setContactPerson(e.target.value)}
                  type="text"
                  name="last-name"
                  id="last-name"
                  autocomplete="family-name"
                  class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
                />
              </div>
            </div>{" "}
            <div class="sm:col-span-3">
              <label
                for="first-name"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Equipement type
              </label>
              <div class="mt-2">
                <input
                  value={equipement_type}
                  onChange={(e) => setEquipementType(e.target.value)}
                  type="text"
                  name="first-name"
                  id="first-name"
                  autocomplete="given-name"
                  class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
                />
              </div>
            </div>
            <div class="sm:col-span-3">
              <label
                for="first-name"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Service station
              </label>
              <div class="mt-2">
                <input
                  value={localisation}
                  onChange={(e) => setLocalisation(e.target.value)}
                  type="text"
                  name="first-name"
                  id="first-name"
                  autocomplete="given-name"
                  class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
                />
              </div>
            </div>
            <div class="sm:col-span-3">
              <label
                for="last-name"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Garantie End Date
              </label>
              <input
                value={EndGarantie_Date}
                onChange={(e) => setEndGarantieDate(e.target.value)}
                type="date"
                name="first-name"
                id="first-name"
                autocomplete="given-name"
                class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
              />
              <div class="mt-2"></div>
            </div>{" "}
            <div class="sm:col-span-3">
              <label
                for="first-name"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Garantie Start Date
              </label>
              <input
                value={StartGarantie_Date}
                onChange={(e) => setStartGarantieDate(e.target.value)}
                type="date"
                name="first-name"
                id="first-name"
                autocomplete="given-name"
                class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
              />
              <div class="mt-2"></div>
            </div>
            <div class="col-span-full">
              <label
                for="about"
                class="block  font-medium leading-6 text-gray-900 dark:text-gray-600"
              >
                Description
              </label>
              <div class="mt-2">
                <textarea
                  id="about"
                  name="about"
                  rows="3"
                  class="sm: block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:leading-6"
                ></textarea>
              </div>
            </div>
          </div>
        </div>
      </div>
      {isTechnicienModalOpen && (
        <TechnicienList handleClose={closeTechnicienModal}  handleTechnicienSelection={handleClientSelection} />
      )}
      {isEquipementModalOpen && (
        <EquipementList handleClose={closeEquipementModal}  handleEquipementSelection={handleClientSelection}  />
      )}

      <div class="mt-6 flex items-center justify-end gap-x-6">
        <button type="button" class=" font-semibold leading-6 text-gray-900">
          Cancel
        </button>
        <button
          onClick={Add}
          type="submit"
          class="rounded-md bg-indigo-600 px-3 py-2  font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
        >
          Save
        </button>
      </div>
    </>
  );
};
export default AddFieldTicket;
