import React, { useState, useEffect, useRef } from 'react'; // Ajouter "useEffect", "useRef" ici

import 'react-toastify/dist/ReactToastify.css';
import { toast, ToastContainer } from 'react-toastify';
import { useAuthContext } from 'views/auth/hooks/useAuthContext';
import { saveAs } from 'file-saver';
import domtoimage from 'dom-to-image';
import { CSSRulePlugin } from "gsap/dist/CSSRulePlugin";
import gsap from "gsap";

const Intervention = ({ handleClose, reclamation }) => {
const { user } = useAuthContext();
const { ticketId } = useParams();
const [ticketDetails, setticketDetails] = useState(null);
useEffect(() => {
    if (ticketId) {
        fetch(`/api/ticket/${ticketId}`)
            .then((response) => response.json())
            .then((data) => setticketDetails(data))
            .catch((error) =>
                console.error("Error fetching ticket details:", error)
            );
    }
}, [ticketId]);
if (!ticketDetails) {
    return <div>Loading...</div>;
}
console.log(ticketDetails);
const cardRef = useRef(null);


// Register the CSSRulePlugin with GSAP
gsap.registerPlugin(CSSRulePlugin);

const handleDownload = () => {
  if (cardRef.current) {
    const proxyUrl = 'https://crossorigin.me/'; 

    domtoimage.toPng(cardRef.current, { cors: proxyUrl })
      .then(function (dataUrl) {
        saveAs(dataUrl, `reclamation_${reclamation?._id}.png`);
        window.scrollTo(0, 0);
      })
      .catch(function (error) {
        console.error('Error:', error);
      });
  }
};




/*useEffect(() => {
  setNumRapport(reclamation?._id || '');
