  setType(reclamation?.type || '');
  setDateDebut(reclamation?.createdAt || '');
  setDateFin(reclamation?.dateFin || '');
  setDescription(reclamation?.description || '');
  console.log(cardRef.current);
  //handleDownload()

}, [reclamation]);*/
