    setSelectedEquipementIds((prevSelectedIds) => {
      if (prevSelectedIds.includes(equipementId)) {
        return prevSelectedIds.filter((id) => id !== equipementId);
      } else {
        return [...prevSelectedIds, equipementId];
      }
    });
    console.log(equipementId);
