import React, { useState, useEffect, useRef } from 'react'; // Ajouter "useEffect", "useRef" ici

import 'react-toastify/dist/ReactToastify.css';
import { toast, ToastContainer } from 'react-toastify';
import { useAuthContext } from 'views/auth/hooks/useAuthContext';
import { saveAs } from 'file-saver';
import domtoimage from 'dom-to-image';
import { CSSRulePlugin } from "gsap/dist/CSSRulePlugin";
import gsap from "gsap";

const Intervention = ({ handleClose, reclamation }) => {
const { user } = useAuthContext();

const cardRef = useRef(null);


// Register the CSSRulePlugin with GSAP
gsap.registerPlugin(CSSRulePlugin);

const handleDownload = () => {
  if (cardRef.current) {
    const proxyUrl = 'https://crossorigin.me/'; 

    domtoimage.toPng(cardRef.current, { cors: proxyUrl })
      .then(function (dataUrl) {
        saveAs(dataUrl, `reclamation_${reclamation?._id}.png`);
        window.scrollTo(0, 0);
      })
      .catch(function (error) {
        console.error('Error:', error);
      });
  }
};




/*useEffect(() => {
  setNumRapport(reclamation?._id || '');
  setClient(reclamation?.client || '');
  setType(reclamation?.type || '');
  setDateDebut(reclamation?.createdAt || '');
  setDateFin(reclamation?.dateFin || '');
  setDescription(reclamation?.description || '');
  console.log(cardRef.current);
  //handleDownload()

}, [reclamation]);*/
  return (

    <div  className="rounded-md fixed p-2 main   mt-5 center border-2 shadow-lg items-center bg-white border-tunisys-100">
